// Input: Telegram update in $json.message.text
// Output: {days, query, chatId, isUpdate}
const text = ($json?.message?.text || '').trim();
const chatId = $json?.message?.chat?.id;
// Pattern: 'update me' or 'update me 21'
const m = text.match(/^update\s+me(?:\s+(\d+))?$/i);
const days = m && m[1] ? parseInt(m[1], 10) : 14;
const isUpdate = !!m;
const query = `from:007gouravjha@gmail.com newer_than:${isUpdate ? days : 14}d`;
return [{ json: { text, chatId, isUpdate, days: isNaN(days) ? 14 : days, query } }];

// Aggregate Gmail messages into readable snippets with date
const items = $items();
const trimmed = items.slice(0, 30);

function headerValue(headers, name) {
  if (!Array.isArray(headers)) return '';
  const h = headers.find(x => x && x.name && x.name.toLowerCase() === name.toLowerCase());
  return h ? h.value : '';
}

function extractBody(payload) {
  if (!payload) return '';
  const parts = payload.parts || [];
  for (const p of parts) {
    if (p.mimeType === 'text/plain' && p.body?.data) {
      try {
        return Buffer.from(p.body.data.replace(/-/g, '+').replace(/_/g, '/'), 'base64').toString('utf8');
      } catch(e){}
    }
  }
  return payload.body?.data ? Buffer.from(payload.body.data, 'base64').toString('utf8') : '';
}

const lines = trimmed.map((it, i) => {
  const j = it.json || {};
  const headers = j.payload?.headers || [];
  const subject = headerValue(headers, 'Subject') || '(no subject)';
  const dateRaw = headerValue(headers, 'Date') || '';
  const date = dateRaw ? new Date(dateRaw).toDateString() : '(no date)';
  const from = headerValue(headers, 'From') || '(unknown sender)';
  const body = extractBody(j.payload).slice(0, 500).replace(/\s+/g, ' ').trim();
  const snippet = (j.snippet || '').replace(/\s+/g, ' ').trim();
  return `#${i + 1}\nDate: ${date}\nFrom: ${from}\nSubject: ${subject}\nBody: ${body || snippet}`;
});

const days = $node['Parse Command1'].json.days;
const header = `Summarize emails from 007gouravjha@gmail.com for the last ${days} days. Include exact dates, subjects, senders, and main content summaries.`;
const body = lines.length ? lines.join('\n\n') : 'NO_RESULTS';
return [{ json: { prompt: `${header}\n\n${body}` } }];
